import React from 'react';

import styled from 'styled-components';

const Text = styled.div`
    font-size: 24px;
    color: red;
    text-align: center;
    border: ${({ isActive }) => isActive ? '1px solid black' : '2px dotted green'};
    cursor: pointer;
`;

const Testing = () => (
    <div>
        <Text isActive={false}>I am CSS in JS</Text>
    </div>
)

export default Testing;
